import React, { useState, useEffect } from 'react';
import { useWebSocket } from '../hooks/useWebSocket';
import { apiService } from '../services/apiService';
import Header from './Header';
import AlertsCard from './AlertsCard';
import PositionsCard from './PositionsCard';
import MarketDataCard from './MarketDataCard';
import TradesCard from './TradesCard';
import RiskMetricsCard from './RiskMetricsCard';
import { Activity, TrendingUp, AlertTriangle, Users, DollarSign } from 'lucide-react';

const Dashboard = () => {
  const [alerts, setAlerts] = useState([]);
  const [positions, setPositions] = useState([]);
  const [marketData, setMarketData] = useState([]);
  const [recentTrades, setRecentTrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const { isConnected, subscribe, unsubscribe } = useWebSocket();

  // Set page title
  useEffect(() => {
    document.title = 'Sentinel - Risk Management System';
  }, []);

  // Load initial data
  useEffect(() => {
    const loadInitialData = async () => {
      try {
        setLoading(true);
        const [alertsRes, positionsRes, marketRes, tradesRes] = await Promise.all([
          apiService.getAlerts(),
          apiService.getPositions(),
          apiService.getMarketOverview(),
          apiService.getTrades(),
        ]);

        setAlerts(alertsRes.data);
        setPositions(positionsRes.data);
        setMarketData(marketRes.data);
        setRecentTrades(tradesRes.data ? tradesRes.data.slice(0, 10) : []);
      } catch (err) {
        setError('Failed to load initial data');
        console.error('Error loading initial data:', err);
      } finally {
        setLoading(false);
      }
    };

    loadInitialData();
  }, []);

  // WebSocket subscriptions
  useEffect(() => {
    if (isConnected) {
      // Subscribe to real-time alerts
      subscribe('/topic/alerts', (newAlert) => {
        setAlerts(prev => [newAlert, ...prev]);
      });

      // Subscribe to real-time positions
      subscribe('/topic/positions', (updatedPositions) => {
        setPositions(updatedPositions);
      });

      return () => {
        unsubscribe('/topic/alerts');
        unsubscribe('/topic/positions');
      };
    }
  }, [isConnected, subscribe, unsubscribe]);

  // Periodic data refresh
  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const [alertsRes, positionsRes, marketRes, tradesRes] = await Promise.all([
          apiService.getAlerts(),
          apiService.getPositions(),
          apiService.getMarketOverview(),
          apiService.getTrades(),
        ]);

        setAlerts(alertsRes.data);
        setPositions(positionsRes.data);
        setMarketData(marketRes.data);
        setRecentTrades(tradesRes.data ? tradesRes.data.slice(0, 10) : []);
      } catch (err) {
        console.error('Error refreshing data:', err);
      }
    }, 60000); // Refresh every minute

    return () => clearInterval(interval);
  }, []);

  const handleAcknowledgeAlert = async (alertId) => {
    try {
      await apiService.acknowledgeAlert(alertId);
      setAlerts(prev => prev.filter(alert => alert.id !== alertId));
    } catch (err) {
      console.error('Error acknowledging alert:', err);
    }
  };

  const handleRefreshMarketData = async () => {
    try {
      const response = await apiService.getMarketOverview();
      setMarketData(response.data);
    } catch (err) {
      console.error('Error refreshing market data:', err);
    }
  };

  const handleRefreshTrades = async () => {
    try {
      const response = await apiService.getTrades();
      setRecentTrades(response.data ? response.data.slice(0, 10) : []);
    } catch (err) {
      console.error('Error refreshing trades:', err);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          {/* Loading Logo */}
          <div className="w-16 h-16 mx-auto mb-4">
            <svg viewBox="0 0 40 40" className="w-full h-full animate-pulse">
              <rect width="40" height="40" fill="black"/>
              <rect x="8" y="4" width="12" height="8" fill="#FFD700"/>
              <rect x="10" y="2" width="2" height="2" fill="#FFD700"/>
              <rect x="14" y="2" width="2" height="2" fill="#FFD700"/>
              <rect x="18" y="2" width="2" height="2" fill="#FFD700"/>
              <rect x="20" y="4" width="12" height="8" fill="#F4A460"/>
              <rect x="22" y="2" width="2" height="2" fill="#F4A460"/>
              <rect x="26" y="2" width="2" height="2" fill="#F4A460"/>
              <rect x="30" y="2" width="2" height="2" fill="#F4A460"/>
              <polygon points="8,12 32,12 28,32 12,32" fill="#2C2C2C"/>
              <polygon points="12,18 16,18 14,22" fill="white"/>
              <polygon points="24,18 28,18 26,22" fill="#FF0000"/>
            </svg>
          </div>
          <p className="text-gray-600">Loading Sentinel Dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="h-12 w-12 text-danger-500 mx-auto" />
          <p className="mt-4 text-gray-600">{error}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="mt-4 btn btn-primary"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header isConnected={isConnected} />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
          {/* Alerts Card */}
          <AlertsCard 
            alerts={alerts} 
            onAcknowledge={handleAcknowledgeAlert} 
          />

          {/* Positions Card */}
          <PositionsCard positions={positions} />

          {/* Risk Metrics Card */}
          <RiskMetricsCard 
            alerts={alerts} 
            positions={positions} 
          />

          {/* Market Data Card */}
          <MarketDataCard 
            marketData={marketData} 
            onRefresh={handleRefreshMarketData} 
          />

          {/* Trades Card */}
          <TradesCard 
            trades={recentTrades} 
            onRefresh={handleRefreshTrades} 
          />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
